%jdbc(hive_interactive)
DROP DATABASE IF EXISTS cloudair CASCADE;
CREATE DATABASE cloudair;

%jdbc(hive_interactive)
DROP TABLE IF EXISTS cloudair.eu_country_stage;

CREATE EXTERNAL TABLE cloudair.eu_country_stage(
  country_name STRING,
  country_code STRING,
  region STRING
  )
ROW FORMAT SERDE 'org.apache.hive.hcatalog.data.JsonSerDe'
LOCATION '/data/cloudair/eu_country';

%jdbc(hive_interactive)

DROP TABLE IF EXISTS cloudair.eu_country;

CREATE TABLE cloudair.eu_country(
  country_name STRING,
  country_code STRING,
  region STRING
  )
STORED AS ORC;

%jdbc(hive_interactive)
INSERT OVERWRITE Table cloudair.ww_customer
SELECT * FROM cloudair.ww_customer_stage;
